session_data.user_stylesheet:
:root {
  --base: #232136;
  --surface: #2a273f;
  --overlay: #393552;
  --muted: #6e6a86;
  --subtle: #908caa;
  --text: #e0def4;
  --love: #eb6f92;
  --gold: #f6c177;
  --rose: #ea9a97;
  --pine: #3e8fb0;
  --foam: #9ccfd8;
  --iris: #c4a7e7;
  --highlight-low: #2a283e;
  --highlight-med: #44415a;
  --highlight-high: #56526e;
}

[hidden] {
  display: none;
}

a {
  color: var(--pine);
}

a:active,
a:hover {
  outline: 0;
}

a:focus {
  outline: thin dotted;
}

a:hover {
  color: var(--foam);
}

a:visited {
  color: var(--iris);
}

abbr[title] {
  border-bottom: 1px dotted;
}

article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
nav,
section,
summary {
  display: block;
}

audio,
canvas,
video {
  display: inline-block;
}

audio:not([controls]) {
  display: none;
  height: 0;
}

b,
strong {
  font-weight: bold;
}

body {
  font-family: 'Cartograph CF';
  background-color: var(--base);
  color: var(--text);
  padding: 10px;
  word-wrap: break-word;
  margin: 0 auto;
  border: 1pt solid var(--highlight-high);
  padding: 1em;
}


button,
html input[type=\"button\"],
input[type=\"reset\"],
input[type=\"submit\"] {
  -webkit-appearance: button;
  cursor: pointer;
}

button,
input {
  line-height: normal;
}

button,
input,
select,
textarea {
  font-family: inherit;
  font-size: 100%;
  margin: 0;
}

button::-moz-focus-inner,
input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

button[disabled],
input[disabled] {
  cursor: default;
}

code {
  background-color: #073642;
  padding: 2px;
}

code,
kbd,
pre,
samp {
  font-family: monospace, serif;
}

dfn {
  font-style: italic;
}

fieldset {
  border: 1px solid #c0c0c0;
  margin: 0 2px;
  padding: 0.35em 0.625em 0.75em;
}

figure {
  margin: 0;
}

h1 {
  font-size: 2.8em;
}

h1 {
  font-size: 2em;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: 'Cartograph CF';
  text-align: center;
  color: var(--rose);
  font-weight: 400;
}

h2 {
  font-size: 2.4em;
}

h3 {
  font-size: 1.8em;
}

h4 {
  font-size: 1.4em;
}

h5 {
  font-size: 1.3em;
}

h6 {
  font-size: 1.15em;
}

html {
  background-color: var(--base);
  color: var(--text);
}

img {
  border: 0;
}

input[type=\"checkbox\"],
input[type=\"radio\"] {
  box-sizing: border-box;
  padding: 0;
}

input[type=\"search\"] {
  -webkit-appearance: textfield;
  -moz-box-sizing: content-box;
  -webkit-box-sizing: content-box;
  box-sizing: content-box;
}

input[type=\"search\"]::-webkit-search-cancel-button,
input[type=\"search\"]::-webkit-search-decoration {
  -webkit-appearance: none;
}

legend {
  border: 0;
  padding: 0;
}

mark {
  background: #ff0;
  color: #000;
}

pre code {
  background-color: #002b36;
}

pre {
  background-color: #002b36;
  color: #839496;
  border: 1pt solid #586e75;
  padding: 1em;
  box-shadow: 5pt 5pt 8pt #073642;
}

pre {
  white-space: pre-wrap;
  word-wrap: break-word;
}

pre,
code {
  font-family: 'Cartograph CF', sans-serif;
}

q {
  quotes: \"\\201C\" \"\\201D\" \"\\2018\" \"\\2019\";
}

small {
  font-size: 80%;
}

sub {
  bottom: -0.25em;
}

sub,
sup {
  font-size: 75%;
  line-height: 0;
  position: relative;
  vertical-align: baseline;
}

sup {
  top: -0.5em;
}

svg:not(:root) {
  overflow: hidden;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

textarea {
  overflow: auto;
  vertical-align: top;
}

.ACTIVE {
  -webkit-border-radius: 0.2em;
  -moz-border-radius: 0.2em;
  border-radius: 0.2em;
  background-color: #268bd2;
}

.CANCELLED {
  -webkit-border-radius: 0.2em;
  -moz-border-radius: 0.2em;
  border-radius: 0.2em;
  background-color: #859900;
}

.DONE {
  -webkit-border-radius: 0.2em;
  -moz-border-radius: 0.2em;
  border-radius: 0.2em;
  background-color: #859900;
}

.HOLD {
  -webkit-border-radius: 0.2em;
  -moz-border-radius: 0.2em;
  border-radius: 0.2em;
  background-color: #d33682;
}

.NEXT {
  -webkit-border-radius: 0.2em;
  -moz-border-radius: 0.2em;
  border-radius: 0.2em;
  background-color: #268bd2;
}

.NOTE {
  -webkit-border-radius: 0.2em;
  -moz-border-radius: 0.2em;
  border-radius: 0.2em;
  background-color: #d33682;
}

.TODO {
  -webkit-border-radius: 0.2em;
  -moz-border-radius: 0.2em;
  border-radius: 0.2em;
  background-color: #2aa198;
}

.WAITING {
  -webkit-border-radius: 0.2em;
  -moz-border-radius: 0.2em;
  border-radius: 0.2em;
  background-color: #cb4b16;
}

.calibre4 {
    font-size: 0.80em;
}

.tag {
  -webkit-border-radius: 0.35em;
  -moz-border-radius: 0.35em;
  border-radius: 0.35em;
}

.tag {
  background-color: #073642;
  color: #d33682;
  padding: 0 0.2em;
}

.todo,
.next,
.done {
  color: #002b36;
  background-color: #dc322f;
  padding: 0 0.2em;
}
